from collections import namedtuple from operator import itemgetter
def smooth(iq_data, alpha=1, today=None): """Perform exponential smoothing with factor `alpha`.
Time period is a day. Each time period the value of `iq` drops `alpha` times. The most recent data is the most valuable one. """ assert 0 < alpha <= 1
if alpha == 1: # no smoothing return sum(map(itemgetter(1), iq_data))
if today is None: today = max(map(itemgetter(0), iq_data))
return sum(alpha**((today - date).days) * iq for date, iq in iq_data)
IQData = namedtuple("IQData", "date iq")
