import sys import py_compile import compileall
class ReportProblem: def __nonzero__(self): type, value, traceback = sys.exc_info() if type is not None and issubclass(type, py_compile.PyCompileError): print "Problem with", repr(value) raise type, value, traceback return 1 report_problem = ReportProblem()
compileall.compile_dir(".", quiet=report_problem)

def XinY_Stack(x, y):
    stack = [(x, 0, [0]*y)]
    while stack:
        x, index, slots = stack.pop()
        if (y - index) == 1:
            slots[index] = x
            print slots
            slots[index] = 0
        else:
            for i in range(x + 1):
                slots[index] = x-i
                stack.append((i, index + 1, slots[:]))

def XinY_Product(nmarbles, nslots):
    return (slots
            for slots in product(xrange(nmarbles + 1), repeat=nslots)
            if sum(slots) == nmarbles)

def reprunicode(u):
    return repr(u).decode('raw_unicode_escape')

