import datetime as DT import itertools
start_date=DT.date(2008,5,5)
def mkdate(datestring): return DT.datetime.strptime(datestring, "%Y-%m-%d").date()
def fortnight(date): return (date-start_date).days //14
raw = ("2010-08-01", "2010-06-25", "2010-07-01", "2010-07-08") transactions=[(date,"Some data") for date in map(mkdate,raw)] transactions.sort(key=lambda (date,data):date)
for key,grp in itertools.groupby(transactions,key=lambda (date,data):fortnight(date)): print(key,list(grp))

import operator
transactions.sort(key=operator.itemgetter(0))

def transaction_fortnight(transaction):
    date,data=transaction
    return fortnight(date)

for key,grp in itertools.groupby(transactions,key=transaction_fortnight):
    print(key,list(grp))

