from functools import wraps
import time

def timed(f):
    """Time a function."""
    @wraps(f)
    def wrapper(*args, **kwds):
        start = time.clock()
        result = f(*args)
        end = 1000 * (time.clock() - start)
        print '%s: %.3f ms' % (f.func_name, end)
        return result
    return wrapper

