class GeneratorThread(threading.Thread): #Thread for generating value pairs def __init__(self,data): threading.Thread.__init__(self) self.datamutex=datamutex # save reference to the mutex. self.data=data def run(self): while True: #Pick a new first number num=random.randint(0,100) self.datamutex.acquire() # get the mutex. self.data[0]=num #simulate some processing #to calculate second number time.sleep(1) #Place second value into ata self.data[1]=num*4 self.datamutex.release() # release it to allow other thread # to run now that data is consistent. time.sleep(1)

