from twisted.internet.protocol import DatagramProtocol from twisted.internet import reactor from twisted.internet.task import LoopingCall import sys, time
class HeartbeatSender(DatagramProtocol): def __init__(self, name, host, port): self.name = name self.loopObj = None self.host = host self.port = port
def startProtocol(self): # Called when transport is connected # I am ready to send heart beats self.loopObj = LoopingCall(self.sendHeartBeat) self.loopObj.start(2, now=False)
def stopProtocol(self): "Called after all transport is teared down" pass
def datagramReceived(self, data, (host, port)): print "received %r from %s:%d" % (data, host, port)
def sendHeartBeat(self): self.transport.write(self.name, (self.host, self.port))
