import datetime import dateutil.rrule as drrule import dateutil.relativedelta as drel import pprint
def dt2d(date): ''' Convert a datetime.datetime to datetime.date object ''' return datetime.date(date.year,date.month,date.day)
def enumerate_dates(start,end): days=map(dt2d,drrule.rrule(drrule.DAILY, dtstart=start, until=end))
# Find the Monday on or before start start_week=start+drel.relativedelta(weekday=drel.MO(-1)) end_week=end+drel.relativedelta(weekday=drel.MO(-1)) weeks=map(dt2d,drrule.rrule(drrule.WEEKLY, dtstart=start_week, until=end_week))
# Find the first day of the month start_month=start.replace(day=1) end_month=end.replace(day=1) months=map(dt2d,drrule.rrule(drrule.MONTHLY, dtstart=start_month, until=end_month)) return days,weeks,months
