def merge(a, b):
    """Merges two iterators a and b, returning a single iterator that yields
    the elements of a and b in non-decreasing order.  a and b are assumed to each
    yield their elements in non-decreasing order."""

    done = object()
    aNext = next(a, done)
    bNext = next(b, done)

    while (aNext is not done) or (bNext is not done):
        if (bNext is done) or ((aNext is not done) and (aNext < bNext)):
            yield aNext
            aNext = next(a, done)
        else:
            yield bNext
            bNext = next(b, done)

for i in merge(iter(a), iter(b)):
    print i

def merge(*iterators): """Merges a collection of iterators, returning a single iterator that yields the elements of the original iterators in non-decreasing order. Each of the original iterators is assumed to yield its elements in non-decreasing order."""
done = object() n = [next(it, done) for it in iterators]
while any(v is not done for v in n): v, i = min((v, i) for (i, v) in enumerate(n) if v is not done) yield v n[i] = next(iterators[i], done)

