def is_match(word, match):
    # Test if all the characters in word are also in match.
    return all(c in match for c in word)

def has_any_match(main, match):
    # Test if there is any word in main that matches.
    return any(is_match(word, match) for word in main)

def execute(command):
    process = subprocess.Popen(command, shell=True, stdout=subprocess.PIPE, stderr=subprocess.STDOUT)

    # Poll process for new output until finished
    while True:
        nextline = process.stdout.readline()
        if nextline == '' and process.poll() != None:
            break
        sys.stdout.write(nextline)
        sys.stdout.flush()

    output = process.communicate()[0]
    exitCode = process.returncode

    if (exitCode == 0):
        return output
    else:
        raise ProcessException(command, exitCode, output)

#!/usr/bin/env python """Start process; wait 2 seconds; kill the process; print all process output.""" import subprocess import tempfile import time
def main(): # open temporary file (it automatically deleted when it is closed) # `Popen` requires `f.fileno()` so `SpooledTemporaryFile` adds nothing here f = tempfile.TemporaryFile()
# start process, redirect stdout p = subprocess.Popen(["top"], stdout=f)
# wait 2 seconds time.sleep(2)
# kill process #NOTE: if it doesn't kill the process then `p.wait()` blocks forever p.terminate() p.wait() # wait for the process to terminate otherwise the output is garbled
# print saved output f.seek(0) # rewind to the beginning of the file print f.read(), f.close()
if __name__=="__main__": main()

import collections import subprocess import time import threading
def read_output(process, append): for line in iter(process.stdout.readline, ""): append(line)
def main(): # start process, redirect stdout process = subprocess.Popen(["top"], stdout=subprocess.PIPE, close_fds=True) try: # save last `number_of_lines` lines of the process output number_of_lines = 200 q = collections.deque(maxlen=number_of_lines) # atomic .append() t = threading.Thread(target=read_output, args=(process, q.append)) t.daemon = True t.start()
# time.sleep(2) finally: process.terminate() #NOTE: it doesn't ensure the process termination
# print saved lines print ''.join(q)
if __name__=="__main__": main()

import collections import signal import subprocess
class Alarm(Exception): pass
def alarm_handler(signum, frame): raise Alarm
def main(): # start process, redirect stdout process = subprocess.Popen(["top"], stdout=subprocess.PIPE, close_fds=True)
# set signal handler signal.signal(signal.SIGALRM, alarm_handler) signal.alarm(2) # produce SIGALRM in 2 seconds
try: # save last `number_of_lines` lines of the process output number_of_lines = 200 q = collections.deque(maxlen=number_of_lines) for line in iter(process.stdout.readline, ""): q.append(line) signal.alarm(0) # cancel alarm except Alarm: process.terminate() finally: # print saved lines print ''.join(q)
if __name__=="__main__": main()

import collections import subprocess import threading
def main(): # start process, redirect stdout process = subprocess.Popen(["top"], stdout=subprocess.PIPE, close_fds=True)
# terminate process in timeout seconds timeout = 2 # seconds timer = threading.Timer(timeout, process.terminate) timer.start()
# save last `number_of_lines` lines of the process output number_of_lines = 200 q = collections.deque(process.stdout, maxlen=number_of_lines) timer.cancel()
# print saved lines print ''.join(q),
if __name__=="__main__": main()

import sys

memo = set()

def getsizeof(memo, obj):
    location = id(obj)
    if location in memo:
        return 0
    memo.add(location)
    total = sys.getsizeof(obj)
    if isinstance(obj, dict):
        for key in obj:
            total += getsizeof(memo, key)
            total += getsizeof(memo, obj[key])
    elif isinstance(obj, (list, tuple)):
        for item in obj:
            total += getsizeof(memo, item)
    elif isinstance(obj, (int, str)):
        pass
    elif hasattr(obj, '__slots__') and not hasattr(obj, '__dict__'):
        for name in obj.__slots__:
            total += getsizeof(memo, getattr(obj, name))
    else:
        raise TypeError('Unknown: ' + repr(type(obj)))
    return total

