class MyBasePage(webapp.RequestHandler): def __init__(self): # common setup/init stuff here, # omitted for this discussion
def Setup(self): # request handling setup code needed in both GET/POST methods, like # checking for user login, getting session cookies, etc. # omitted for this discussion
def get(self, *args): self.Setup() # call the derived class' 'DoGet' method that actually has # the logic inside it self.DoGet(*args)
def post(self, *args): self.Setup() # call the derived class' 'DoPost' method self.DoPost(*args)
def DoGet(self, *args): ''' derived classes override this method and put all of their GET logic inside. Base class does nothing.''' pass
def DoPost(self, *args): ''' derived classes override this method and put all of their POST logic inside. Base class does nothing.''' pass

class Base_Metaclass(type):
    def __new__(meta, classname, bases, class_dict):
        new_type = type.__new__(meta, classname, bases, class_dict)
        if not (new_type.NOMINATOR % new_type.DENOMINATOR) == 0:
            raise Exception("Invalid subclass created - validation failed")
        return new_type

# have Base and all its descendants be enforced:
class Base(object):
    __metaclass__ = Base_Metaclass
    # I must pass the validation myself, no None's anymore...
    NOMINATOR = 1
    DENOMINATOR = 1

Xwt = Xw - Xc;
Ywt = Yw - Yc;
Zwt = Zw;

(Xc,Yc,Zc) = camera position
(Xw,Yw,Zw) = world coordinates of object in the scene
(Xwt,Ywt,Zwt) = world coordinates of object translated to camera position

Xwc =  Xwt * Cos(psi) + Ywt * Sin(psi);
Ywc = -Xwt * Sin(psi) + Ywt * Cos(psi);
Zwc =  Zwt

Psi = angle of camera rotation
(Xwc,Ywc,Zwc) = world coordinates of object transformed to camera orientation

