def curl_limit_rate(url, filename, rate_limit): """Rate limit in bytes""" import pycurl c = pycurl.Curl() c.setopt(c.URL, url) c.setopt(c.MAX_RECV_SPEED_LARGE, rate_limit) if os.path.exists(filename): file_id = open(filename, "ab") c.setopt(c.RESUME_FROM, os.path.getsize(filename)) else: file_id = open(filename, "wb")
c.setopt(c.WRITEDATA, file_id) c.setopt(c.NOPROGRESS, 0) c.setopt(c.PROGRESSFUNCTION, curl_progress) c.perform()

import timeit

def fib(x):
    if x==0 or x==1: return 1
    else: return fib(x-1)+fib(x-2)

print timeit.Timer('fib(5)', 'from __main__ import fib').timeit()

def fibTimeLimited(limit):
  start = time.time()
  n, f0, f1 = 1, 0, 1
  while time.time() < start + limit:
    n += 1
    f0, f1 = f1, f0+f1
  return (n, f1)

def split_special(s):
    d = { ':)': '<HAPPY_SMILEY>', ':(': '<SAD_SMILEY>'}
    for smiley, placeholder in d.iteritems():
        s = s.replace(smiley, placeholder)
    s = ''.join(filter(lambda c: c not in '.,!', list(s)))
    for smiley, placeholder in d.iteritems():
        s = s.replace(placeholder, smiley)
    return s.split()

