from collections import defaultdict
summer = defaultdict(int)
mini, maxi = 0,0
for d in all.values():
    for (name, start, stop), value in d.iteritems(): 
        # im completely ignoring the `name` here, not sure if that's what you want
        # else just separate the data before doing this ...
        if mini == 0:
            mini = start
        mini, maxi = min(mini, start), max(maxi, stop)
        for i in range(start, stop+1):
            summer[i]+=value

# now we have the values at each point, very redundant but very fast so  far
print summer

# now we can find the intervals:
def get_intervals(points, start, stop):
    cstart = start
    for i in range(start, stop+1):
        if points[cstart] != points[i]: # did the value change ?
            yield cstart, i-1, points[cstart]
            cstart = i

    if cstart != i:
        yield cstart, i, points[cstart]


print list(get_intervals(summer, mini, maxi))

from collections import defaultdict from heapq import heappush, heappop
class Summer(object): def __init__(self): # its a priority queue, kind of like a sorted list self.hq = []
def additem(self, start, stop, value): # at `start` add it as a positive value heappush(self.hq, (start, value)) # at `stop` subtract that value again heappush(self.hq, (stop, -value))
def intervals(self): hq = self.hq start, val = heappop(hq) while hq: point, value = heappop(hq) yield start, point, val # just maintain the current value and where the interval started val += value start = point assert val == 0
summers = defaultdict(Summer) for d in all.values(): for (name, start, stop), value in d.iteritems(): summers[name].additem(start, stop, value)
for name,s in summers.iteritems(): print name, list(s.intervals())

