list_of_extensions = ['flv','mp4','avi'] # Supplement it with what you find.
list_of_files = []


import os

def callback(arg, directory, files):
    for file in files:
        extn = file.split('.')[-1]
        if extn in arg:
               list_of_files.append(os.path.join(directory,file))

os.path.walk(".", callback, list_of_extensions)

import re

def tryint(s):
    try:
        return int(s)
    except:
        return s

def alphanum_key(s):
    """ Turn a string into a list of string and number chunks.
        "z23a" -> ["z", 23, "a"]
    """
    return [ tryint(c) for c in re.split('([0-9]+)', s) ]

def sort_nicely(l):
    """ Sort the given list in the way that humans expect.
    """
    l.sort(key=alphanum_key)

from django.utils.functional import wraps

def beta(view):
    @wraps(view)
    def inner(request, *args, **kwargs):
        if request.user.user_profile.beta:
            return view(request, *args, **kwargs)
        # Up to you how you return failure...
    return inner

@beta
def my_view(request):
    # do something new here.

@user_passes_test(lambda u: u.profile.beta)
def my_view(request):
    # do something clever

