def expose(self, widget, event):
cr = widget.window.cairo_create()
cr.set_source_surface(self.surface, 10, 10) cr.paint_with_alpha(self.alpha)
self.alpha += self.delta
if self.alpha >= 1: return False else: gobject.timeout_add(50,self.fadeImage)
