def progress(x): global progress_x x = int(x * 40 // 100) sys.stdout.write("#" * (x - progress_x)) sys.stdout.flush() progress_x = x
def endProgress(): sys.stdout.write("#" * (40 - progress_x) + "]\n") sys.stdout.flush()

