from Tkinter import * root=Tk()
class FrameRepeater(object): def __init__(self, start=0, end=4): self.frame = None self.number = start self.end = end
def new_frame(self): if self.frame: self.frame.destroy() self.frame = Frame(root) self.frame.pack() self.content = Label(self.frame, text = 'window ' + str(self.number) + ' content') self.content.pack() self.button = Button(self.frame, text = 'Next ->', command = self.replace) self.button.pack() self.number += 1
def replace(self): if self.number < self.end: self.new_frame() elif self.number >= self.end: self.content.config(text='Press button again to quit') self.button.config(command=self.quit)
def quit(self): self.frame.destroy() root.destroy() exit()
FrameRepeater().new_frame() root.mainloop()

#!/usr/bin/python2

import sys

def colorize(stdoutColor, stderrColor):
  defaultColor = '\033[0;0m'

  def applyColorize(f):
    class colorWrapper(object):
      def __init__(self, wrapee, color):
        self.wrapee = wrapee
        self.color = color
      def __getattr__(self, attr):
        if attr == 'write' and self.wrapee.isatty():
          return lambda x: self.wrapee.write(self.color + x + defaultColor)
        else:
          return getattr(self.wrapee, attr)

    def wrapper(*args, **kwds):
      oldStdout = sys.stdout
      oldStderr = sys.stderr
      sys.stdout = colorWrapper(oldStdout, stdoutColor)
      sys.stderr = colorWrapper(oldStderr, stderrColor)
      try:
        f(*args, **kwds)
      finally:
        sys.stdout = oldStdout
        sys.stderr = oldStderr

    return wrapper

  return applyColorize


greenColor = '\033[01;32m'
redColor = '\033[01;31m'

