@Slot(bool) def process(self, something=False): self.page.setViewportSize(self.page.mainFrame().contentsSize()) frame = self.page.currentFrame() elem = frame.documentElement() self.gather_info(elem) self.finished.emit()
def gather_info(self, elem, i=0): if i > 200: return cnt = 0 while cnt < 100: s = elem.toPlainText() rect = elem.geometry() name = elem.tagName() dim = [rect.x(), rect.y(), rect.x() + rect.width(), rect.y() + rect.height()] if s: self.output.append(dict(pos=(i, cnt), dim=dim, tag=name, text=s)) child = elem.firstChild() if not child.isNull(): self.gather_info(child, i+1) elem = elem.nextSibling() if elem.isNull(): break cnt += 1
