def defined(dct, keys):
    return zip(*[([k] + [v[m] for m in keys])
                 for k, v in dct.iteritems()
                 if all(m in v for m in keys)])

import numpy as np
def main(): np.random.seed(1) # For reproducibility data = generate_data((10, 6))
indices = rightmost_min_col(data) new_data = pop_col(data, indices)
print 'Original data...' print data print 'Modified data...' print new_data
def generate_data(shape): return np.random.randint(0, 10, shape)
def rightmost_min_col(data): nrows, ncols = data.shape[:2] min_indices = np.fliplr(data).argmin(axis=1) min_indices = (ncols - 1) - min_indices return min_indices
def pop_col(data, col_indices): nrows, ncols = data.shape[:2] col_indices = col_indices[:, np.newaxis] row_indices = np.arange(ncols)[np.newaxis, :] mask = col_indices != row_indices return data[mask].reshape((nrows, ncols-1))
