def rem(iterable):
    c = collections.Counter(iterable)
    for k in c:
        c[k] -= 1
    return sorted(c.elements())

def rem(iterable):
   result = []
   seen = set()
   for x in iterable:
       if x in seen:
           result.append(x)
       else:
           seen.add(x)
   result.sort()
   return result

