import functools import types
def metadec(func):
@functools.wraps(func) def wrapper(*args, **kwargs): # do stuff return func(*args, **kwargs)
c = wrapper.func_code fname = "%s__%s" % (func.__name__, wrapper.__name__)
code = types.CodeType( c.co_argcount, c.co_nlocals, c.co_stacksize, c.co_flags, c.co_code, c.co_consts, c.co_names, c.co_varnames, c.co_filename, fname, # change the name c.co_firstlineno, c.co_lnotab, c.co_freevars, c.co_cellvars, )
return types.FunctionType( code, # Use our updated code object wrapper.func_globals, fname, # Use the updated name wrapper.func_defaults, wrapper.func_closure, )

