class SnapShotAnimation(ArtistAnimation):
    def __init__(self, fig, artists, snapshot_delay, *args, **kwargs):
        self._snapshot_delay = snapshot_delay
        self._time_to_snapshot = snapshot_delay
        ArtistAnimation.__init__(self, fig, artists, *args, **kwargs)

    def _step(self, *args):
        if self._time_to_snapshot <= 0:
            do_snapshot() 
            self._time_to_snapshot = self._snap_shot_delay #reset timer
        else:
            self._time_to_snapshot -= self._interval
        ArtistAnimation._step(*args) #ancestor method maybe better at start

    def do_snapshot(self):
        """Your actual snapshot code comes here - basically saving to a output"""
        fname = 'snapshot.png'
        self._fig.savefig(fname)

import operator import itertools import re import math import functools import logging logger = logging.getLogger(__name__)
zip_longest = itertools.izip_longest
def tableinfo(rows, sep = u'â', corner = u'Â·', delim = None, corner_delim = None, prefix = u'â ', postfix = u' â', colsep = u' â ', has_header = False, header = None, separate_rows = False, framed = (True, True), separate_empty_lines = True, justify = 'right', wrapfunc = lambda x:x, width = None, phantom = None, **kw): # Based on: http://code.activestate.com/recipes/267662-table-indentation/ # Author: http://code.activestate.com/recipes/users/2591466/ (George Sakkis) def row_wrapper(row): try: new_rows = [wrapper(item).split('\n') for (item, wrapper) in zip(row, wrapfunc)] except TypeError: # This happens if wrapfunc is not an iterator # TypeError: zip argument #2 must support iteration new_rows = [wrapfunc(item).split('\n') for item in row] return list(zip_longest(*new_rows, fillvalue = u'')) if header: has_header = True rows = itertools.chain(normalize([header]), rows) logical_rows = [row_wrapper(row) for row in rows] columns = zip(*functools.reduce(operator.add, logical_rows)) max_width = ( [max(len(item) for item in column) for column in columns] if width is None else width ) if phantom is not None: max_width = [max(x) for x in zip(phantom, max_width)] lcorner = corner + sep*(len(prefix)-1) if len(prefix) >= 1 else u'' rcorner = sep*(len(postfix)-1) + corner if len(postfix) >= 1 else u''
delim = itertools.repeat(colsep) if delim is None else itertools.cycle(delim) corner_delim = (delim_to_corner(delim, sep, corner) if corner_delim is None else itertools.cycle(corner_delim)) row_separator = (sep*w for w in max_width) row_separator = (lcorner +''.join(list(iterjoin(corner_delim, row_separator))) +rcorner) dispatch = {'center':unicode.center, 'right':unicode.rjust, 'left':unicode.ljust} try: justify = itertools.cycle([dispatch[item] for item in justify]) except KeyError: justify = itertools.repeat(dispatch[justify.lower()]) result = [] for physical_rows in logical_rows: row_result = [] for row in physical_rows: if separate_empty_lines and not ''.join(row).strip(): row_result.append(row_separator) else: pieces = [justifier(item, w) for (item, w, justifier) in zip(row, max_width, justify)] row_result.append( prefix + u''.join(list(iterjoin(delim, pieces))) + postfix ) result.append(u'\n'.join(row_result)) if has_header and not separate_rows: result.insert(1, row_separator) has_header = False joiner = u'\n'+row_separator+u'\n' if separate_rows else u'\n' result = joiner.join(result) top_framed, bottom_framed = framed if top_framed: result = row_separator+u'\n'+result if bottom_framed: result = result+u'\n'+row_separator return result, max_width
def iterjoin(sep, it): sep = itertools.cycle(sep) it = iter(it) yield next(it) for a, b in zip(it, sep): yield b yield a
def normalize(rows): new_rows = [] for row in rows: new_rows.append([unicode(elt).expandtabs() for elt in row]) return new_rows
def delim_to_corner(delim, sep, corner): for d in delim: d = d.replace(u'â', corner).replace(u'|', corner) for c in '< >': d = d.replace(c, sep) yield d
def wrap_onspace(text, width): # written by Mike Brown # http://aspn.activestate.com/ASPN/Cookbook/Python/Recipe/148061 ''' A word-wrap function that preserves existing line breaks and most spaces in the text. Expects that existing line breaks are posix newlines (\n). ''' words = iter(text.split(' ')) line = next(words) for word in words: contemplated_width = (len(line[line.rfind('\n')+1:]) + len(word.split('\n', 1)[0])) if contemplated_width >= width: line += '\n'+word else: line += ' '+word return line
def wrap_onspace_strict(text, width): '''Similar to wrap_onspace, but enforces the width constraint: words longer than width are split.''' word_pat = re.compile(r'\S{'+unicode(width)+r',}') return wrap_onspace(word_pat.sub(lambda m: wrap_always(m.group(), width), text), width)
def wrap_always(text, width): '''A simple word-wrap function that wraps text on exactly width characters. It doesn\'t split the text in words.''' return '\n'.join( text[width*i:width*(i+1)] for i in xrange(int(math.ceil(1.*len(text)/width))) )
def onspace(width): return functools.partial(wrap_onspace, width = width)
def strict(width): return functools.partial(wrap_onspace_strict, width = width)
def always(width): return functools.partial(wrap_always, width = width)
def table(rows, sep = u'â', corner = u'Â·', delim = None, corner_delim = None, prefix = u'â ', postfix = u' â', has_header = False, header = None, separate_rows = False, framed = (True, True), separate_empty_lines = True, justify = 'right', wrapfunc = lambda x:x, width = None, **kw): ''' Â·ââââââââââââââââââââââÂ·âââââââââââââââââââââââââââââââââââââââââââââââââââââââââÂ· â rows â A sequence of sequences of items, one sequence per row. â Â·ââââââââââââââââââââââÂ·âââââââââââââââââââââââââââââââââââââââââââââââââââââââââÂ· â framed â row separator on top and bottom â Â·ââââââââââââââââââââââÂ·âââââââââââââââââââââââââââââââââââââââââââââââââââââââââÂ· â sep â Character to be used for the row separator line (if â â â has_header==True or separate_rows==True). â Â·ââââââââââââââââââââââÂ·âââââââââââââââââââââââââââââââââââââââââââââââââââââââââÂ· â delim â A sequence of column delimiters. The delimiters are â â â repeated in a cycle â Â·ââââââââââââââââââââââÂ·âââââââââââââââââââââââââââââââââââââââââââââââââââââââââÂ· â corner_delim â A sequence of column delimiters used in row separators, â â â repeated in a cycle. â Â·ââââââââââââââââââââââÂ·âââââââââââââââââââââââââââââââââââââââââââââââââââââââââÂ· â prefix â A string prepended to each printed row. â Â·ââââââââââââââââââââââÂ·âââââââââââââââââââââââââââââââââââââââââââââââââââââââââÂ· â postfix â A string appended to each printed row. â Â·ââââââââââââââââââââââÂ·âââââââââââââââââââââââââââââââââââââââââââââââââââââââââÂ· â has_header â True if there is a row separator between the first and â â â second row â Â·ââââââââââââââââââââââÂ·âââââââââââââââââââââââââââââââââââââââââââââââââââââââââÂ· â separate_rows â True if all rows are to be separated â Â·ââââââââââââââââââââââÂ·âââââââââââââââââââââââââââââââââââââââââââââââââââââââââÂ· â framed â True if top (and/or bottom) have a row separator â Â·ââââââââââââââââââââââÂ·âââââââââââââââââââââââââââââââââââââââââââââââââââââââââÂ· â separate_empty_lines â replace empty lines with a row separator â Â·ââââââââââââââââââââââÂ·ââââââââââââââââââââââ