def ascii_table(rows, sep = '-', corner = '+', delim = [' | '], corner_delim = None, prefix = u'| ', postfix = u' |', has_header = False, header = None, separate_rows = False, framed = (True, True), separate_empty_lines = True, justify = 'right', wrapfunc = lambda x:x, width = None, **kw): result, max_width = tableinfo( normalize(rows), sep = sep, corner = corner, delim = delim, corner_delim = corner_delim, prefix = prefix, postfix = postfix, has_header = has_header, header = header, separate_rows = separate_rows, framed = framed, separate_empty_lines = separate_empty_lines, justify = justify, wrapfunc = wrapfunc, width = width) return result

def logistic(x, y0, k, d, a, b):
    if b > 0 and a > 0:
        y = (k * pow(1 + np.exp(d - (a * b * x) ), (-1/b) )) + y0
    elif b >= -1 or b < 0 or a < 0:
        y = (k * pow(1 - np.exp(d - (a * b * x) ), (-1/b) )) + y0
    else:
        y = 1e10
    return y

# lines = lines parsed out of file
line_store = {}
for line in lines:
    tokens = line.split('\t')
    if len(tokens) in line_store:
        line_store[len(tokens)].append(line)
    else:
        line_store[len(tokens)] = [line]
most = []
for line_group in line_store.values():
    if len(line_group) > len(most):
        most = line_group

def listenToClient(self, c):
    while self.server_running:
        try:
            recvd_data = c.client.recv(self.size)
            print "received:", c, recvd_data
            if recvd_data == "" or recvd_data == "close\n":
                print "Client " + str(c.address) + (" disconnected...")
                return

            print recvd_data

        except socket.error, (value, message):
            if value == 35:
                continue 
            else:
                print "Error:", value, message

