def heat(number):
    m = { "r" : 12,
          "b" : 24 }

    return m[number] if number in m else 0.0

import threading
class print_out(threading.Thread):
def __init__ (self, m1, m2): threading.Thread.__init__(self) self.m1 = m1 self.m2 = m2
def run(self): print self.m1 print self.m2 print "\n"
threads = [] for num in range(0, 10): thread = print_out('a', 'b') thread.start() threads.append(thread)
for thread in threads: thread.join()

