class Agent:
  def __init__(self):
    self.blah = whatever
    self.transport = None
  def set_transport(self, transport):
    if self.transport == transport:
      return
    if self.transport is not None:
      self.transport.remove_agent(self)
    self.transport = transport

  def doA(self):
    res = self.transport.doX(self.blah)

