import pygame, random, sys
from pygame.locals import *

pygame.init()

width = 640
height = 480


DISPLAYSURF = pygame.display.set_mode((width, height))
pygame.display.set_caption('It moves!')
pygame.mouse.set_visible(0)



class Player(pygame.sprite.Sprite):

    def __init__(self, x, y):
        pygame.sprite.Sprite.__init__(self)

        self.x = x
        self.y = y
        self.width = 50
        self.height = 25
        self.playerRect = None



    def update(self, event):
        if event.type == MOUSEMOTION:
            self.x, self.y = event.pos


        #get a new playerRect and draw it
        self.playerRect = pygame.Rect(self.x, self.y, self.width, self.height)
        pygame.draw.ellipse(DISPLAYSURF, RED, (self.playerRect), 3)


    def shotcheck(self, event):
        if event.type == KEYDOWN:
            if event.key == K_KP8:
                return (True, 'up')
            elif event.key == K_KP2:
                return (True, 'down')
            elif event.key == K_KP4:
                return (True, 'left')
            elif event.key == K_KP6:
                return (True, 'right')
            elif event.key == K_KP7:
                return (True, 'upleft')
            elif event.key == K_KP1:
                return (True, 'downleft')
            elif event.key == K_KP9:
                return (True, 'upright')
            elif event.key == K_KP3:
                return (True, 'downright')
            else:
                return (0, 0)



class Enemy(pygame.sprite.Sprite):
    def __init__(self, x, y):
        pygame.sprite.Sprite.__init__(self)
        self.x = x
        self.y = y
        #self.body = pygame.rect.Rect(self.x, self.y, 15, 15)
        self.speed = 5
        self.xmove = 0
        self.ymove = 0



    def update(self, event):
        self.x += self.speed
        if self.x > 350:
            self.speed *= -1
        elif self.x < 25:
            self.speed *= -1

        pygame.draw.rect(DISPLAYSURF, BLUE, (self.x, self.y, 15, 15), 4)



#pass it a directional value when fired based on the key
#may have to divide speed / 2 if moving diagonally
class Bullet(pygame.sprite.Sprite):
    def __init__(self, x, y, direction):
        pygame.sprite.Sprite.__init__(self)
        self.x = x
        self.y = y
        self.direction = direction
        self.width = 4
        self.height = 4
        self.bulletRect = None
        self.speed = 8



    def update(self, event):

        if self.direction == 'up':
            self.y -= self.speed

        elif self.direction == 'down':
            self.y += self.speed

        elif self.direction == 'left':
            self.x -= self.speed

        elif self.direction == 'right':
            self.x += self.speed

        elif self.direction == 'upleft':
            self.x -= (self.speed/2)
            self.y -= (self.speed/2)

        elif self.direction == 'downleft':
            self.x -= (self.speed/2)
            self.y += (self.speed/2)

        elif self.direction == 'upright':
            self.x += (self.speed/2)
                self.y -= (self.speed/2)

        elif self.direction == 'downright':
            self.x += (self.speed/2)
            self.y += (self.speed/2)


        self.bulletRect = pygame.Rect(self.x, self.y, 4, 4)
        pygame.draw.ellipse(DISPLAYSURF, GREEN, (self.bulletRect), 2)





FPS = 30
fpsClock = pygame.time.Clock()


RED = (255, 0, 0)
GREEN = (0, 255, 0)
BLUE = (0, 0, 255)
BLACK = (0, 0, 0)



ship = Player(width / 2, height / 2)
bads = Enemy(width / 2, height / 2)



queue = pygame.sprite.Group()
queue.add(ship)
queue.add(bads)


while True:
    DISPLAYSURF.fill(BLACK)
    for event in pygame.event.get():
        if event.type == QUIT:
            sys.exit()


                #passes 'event' to everything in the queue and calls
                #their obj.update().  in this way the gameloop 
                #is a bit more readable
            for thing in queue:
                thing.update(event)

    try: #i'm not married to this bit of code :/
        checkForShot, shotDirection = ship.shotcheck(event)
        if checkForShot:
            shotx, shoty = ship.playerRect.center
            shot = Bullet(shotx, shoty, shotDirection)
            queue.add(shot)
    except TypeError:
        pass

    pygame.display.flip()
    fpsClock.tick(FPS)

def hamiltPath(graph): """This partitions a bipartite graph into a set of components with each component consisting of a hamiltonian path.""" # The maximum degree maxDegree = 2
# Get all the nodes. We will process each of these. remaining = graph.vertices() # Create a new empty graph to which we will add pairs of nodes. newGraph = Graph() # Loop while there's a remaining vertex. while len(remaining) > 0: # Get the next arbitrary vertex. node = remaining.pop() # Now get its neighbors that are in the remaining set. neighbors = [n for n in graph.neighbors(node) if n in remaining] # If this list of neighbors is non empty, then add (node, neighbors[0]) # to the new graph. if len(neighbors) > 0: # If this is not an optimal algorithm, I suspect the selection # a vertex in this indexing step is the crux. Improve this # selection and the algorthim might be optimized, if it isn't # already (optimized in result not time or space complexity). neighbor = neighbors[0] newGraph.addEdge(node, neighbor) # "node" has already been removed from the remaining vertices. # We need to remove "neighbor" if its degree is too high. if len(newGraph.neighbors(neighbor)) >= maxDegree: remaining.remove(neighbor)
return newGraph
class Graph: """A graph that is represented by pairs of vertices. This was created For conciseness, not efficiency"""
def __init__(self): self.graph = set()
def addEdge(self, a, b): """Adds the vertex (a, b) to the graph""" self.graph = self.graph.union({(a, b)})
def neighbors(self, node): """Returns all of the neighbors of a as a set. This is safe to modify.""" return (set(a[0] for a in self.graph if a[1] == node). union( set(a[1] for a in self.graph if a[0] == node) ))
def vertices(self): """Returns a set of all of the vertices. This is safe to modify.""" return (set(a[1] for a in self.graph). union( set(a[0] for a in self.graph) ))
def __repr__(self): result = "\n" for (a, b) in self.graph: result += str(a) + "," + str(b) + "\n" # Remove the leading and trailing white space. result = result[1:-1] return result
graph = Graph() graph.addEdge("0", "4") graph.addEdge("1", "8") graph.addEdge("2", "8") graph.addEdge("3", "5") graph.addEdge("3", "6") graph.addEdge("3", "7") graph.addEdge("3", "8") graph.addEdge("3", "9") graph.addEdge("3", "10") graph.addEdge("3", "11")
print(graph) print() print(hamiltPath(graph)) # Result of this is: # 10,3 # 1,8 # 2,8 # 11,3 # 0,4

import io

def readGridFromPath(self, filepath):
    try:
        self.readGridFromFile(io.open(filepath, 'r', encoding = 'utf-8'))
    except UnicodeDecodeError:
        self.readGridFromFile(io.open(filepath, 'r', encoding = 'utf-16'))

