import Tkinter as tki # Tkinter -> tkinter in Python 3
class MessageBox(tki.Tk):
def __init__(self, info, b0, b1, frame, t, entry): tki.Tk.__init__(self) self.title('Message') # flag for the b0_action method self.entry = entry # save for if the user wants to copy the info to clipboard self.info = info # remove the outer frame if arg frame=False if not frame: self.overrideredirect(True) # default values for the buttons to return self.b0r = True self.b1r = False # if the b0 or b1 args are tuples if isinstance(b0, tuple): b0, self.b0r = b0 if isinstance(b1, tuple): b1, self.b1r = b1 # main frame frame0 = tki.Frame(self, relief='ridge', bd=3, bg='lightgrey') frame0.pack(ipadx=2, ipady=2) # the message label0 = tki.Label(frame0, text=info, bg='lightgrey') label0.pack(padx=8, pady=8) # if arg entry=True create an entry if entry: self.entry0 = tki.Entry(frame0) self.entry0.pack() # button frame frame1 = tki.Frame(frame0, relief='ridge', bd=3, bg='lightgrey') frame1.pack(padx=4, pady=4)
button0 = tki.Button(frame1, width=8, text=b0, relief='groove', bd=4, command=self.b0_action) button0.pack(side='left')
button1 = tki.Button(frame1, width=8, text=b1, relief='groove', bd=4, command=self.b1_action) button1.pack(side='left') # allows the enter button to fire either button's action button0.bind('<KeyPress-Return>', func=self.b0_action) button1.bind('<KeyPress-Return>', func=self.b1_action) # ctrl+c to copy the message self.bind('<Control-c>', func=self.toclip) # center the box on screen self.update_idletasks() xp = (self.winfo_screenwidth() / 2) - (self.winfo_width() / 2) - 8 yp = (self.winfo_screenheight() / 2) - (self.winfo_height() / 2) - 20 glist = [self.winfo_width(), self.winfo_height(), xp, yp] self.geometry('{0}x{1}+{2}+{3}'.format(*glist)) # start with button0 focused button0.focus_set() # allows us to customize what happens when the close button is pressed self.protocol("WM_DELETE_WINDOW", self.xclose) # if arg t is specified: call time_out after t milliseconds if t: self.after(t, func=self.time_out)
def time_out(self): self.v = None self.quit()
def b0_action(self, event=None): # if there is an entry, get its value to return if self.entry: self.v = self.entry0.get() self.quit() else: self.v = self.b0r self.quit()
def b1_action(self, event=None): self.v = self.b1r self.quit()
def toclip(self, event=None): self.clipboard_clear() self.clipboard_append(self.info)
