def create_path(filename): import os fn = os.path.abspath(os.path.join(os.path.dirname(__file__), 'sqlite_files', filename)) return fn
def run_script(filename): path_to_file = create_path(filename)
engine = create_engine('sqlite:///{path}'.format(path=path_to_file), echo=True) Session = sessionmaker(bind=engine) Base = declarative_base(bind=engine)
# object model class MyTable(Base): __tablename__ = 'my_table' __table_args__ = {'autoload': True} id = Column(Integer, primary_key=True) # Monkey patching the id column as primary key
# script itself session = Session() for row in session.query(MyTable).all(): print row return "Stop!"
