conn = sqlite.connect(':memory:') cu = conn.cursor() cu.executescript(""" DROP TABLE IF EXISTS msrun; CREATE TABLE IF NOT EXISTS msrun ( msrun_name VARCHAR(40) PRIMARY KEY NOT NULL , description VARCHAR(500) NOT NULL );
DROP TABLE IF EXISTS feature; CREATE TABLE IF NOT EXISTS feature ( feature_id VARCHAR(40) PRIMARY KEY NOT NULL , msrun_msrun_name VARCHAR(40) NOT NULL , CONSTRAINT fk_feature_msrun1 FOREIGN KEY (msrun_msrun_name ) REFERENCES msrun (msrun_name ) ON DELETE NO ACTION ON UPDATE NO ACTION);
CREATE UNIQUE INDEX id_UNIQUE ON feature (feature_id ASC); CREATE INDEX fk_feature_msrun1 ON feature (msrun_msrun_name ASC);
DROP TABLE IF EXISTS convexhull; CREATE TABLE IF NOT EXISTS convexhull ( convexhull_id INT PRIMARY KEY NOT NULL , mz DOUBLE NOT NULL , rt DOUBLE NOT NULL , feature_feature_id VARCHAR(40) NOT NULL , CONSTRAINT fk_convexhull_feature FOREIGN KEY (feature_feature_id ) REFERENCES feature (feature_id ) ON DELETE NO ACTION ON UPDATE NO ACTION);
CREATE INDEX fk_convexhull_feature ON convexhull (feature_feature_id ASC);
DROP TABLE IF EXISTS position; CREATE TABLE IF NOT EXISTS position ( position_id INT PRIMARY KEY NOT NULL , dim0 INT NOT NULL , dim1 INT NOT NULL , feature_feature_id VARCHAR(40) NOT NULL , CONSTRAINT fk_position_feature1 FOREIGN KEY (feature_feature_id ) REFERENCES feature (feature_id ) ON DELETE NO ACTION ON UPDATE NO ACTION);
CREATE INDEX fk_position_feature1 ON position (feature_feature_id ASC);
