def initUI(self): for i in range(5): l = Label(self, text="Row {0}".format(i)) l.grid(row=i, column=0)
self.b = Button(self, text="Moving button", command=self.move) self.b.grid(row=0, column=1)
self.pack()
root = Tk() app = Example(root) root.mainloop()

