if(current_post.mode == "Multiple"):
#multithreading POST threads = list() for i in range(0, 10): thread = threading.Thread(target = current_post.post) thread.start() threads.append(thread) for thread in threads: thread.join() #calculate the time interval time_interval = time.time() - origin_time print time_interval
if(current_post.mode == "Process"):
#multiprocessing POST processes = list() for i in range(0, 10): process = Process(target=current_post.post) process.start() processes.append(process) for process in processes: process.join() #calculate the time interval time_interval = time.time() - origin_time print time_interval
if(current_post.mode == "Simple"):
#simple POST for i in range(0, 10): current_post.post() #calculate the time interval time_interval = time.time() - origin_time print time_interval

numbers = {1:0}
max = -1
startMax = -1
for i in range(2, 1000 000):
    n = i
    steps = 0
    while n>=i:
        if n&2 == 0:
            n = n/2
        else:
            n = 3*n + 1
        steps = steps + 1
    # n < i, hence it must already be stored in the dictionary
    steps = steps + numbers[n]
    if steps > max:
        max = steps
        startMax = i
    numbers[i] = steps
    return startMax

numbers = {1:0}
max = -1
for i in range(2, 1000 000):
    if i in numbers:
        steps = numbers[i]
    else:
        n = i
        steps = 0
        found = False

        while not found:
            if n&2 == 0:
                n = n/2
            else:
                n = 3*n + 1
            if n in numbers:
                steps = numbers[n]
                found = True
            else:
                newNumbers.append(n)
        # Store all the new numbers into the dictionary
        for num in newNumbers:
            steps = steps + 1
            numbers[num] = steps
    if steps>max:
        max = steps
        startMax = i
return startMax
