def one_of(alternatives):
    return lambda val: val in alternatives

def within(frm, to):
    return lambda val: frm <= val <= to

def gte(const):
    return lambda val: val >= const

def lte(const):
    return lambda val: val <= const

def exact(const):
    return lambda val: val == const


def select(data, **kwargs):
    for item in data:
        if all(chk(item[key]) for key, chk in kwargs.items()):
            yield item

