import sys import os from subprocess import Popen from contextlib import contextmanager
@contextmanager def monitor(stream, callback): read, write = os.pipe() yield write os.close(write) with os.fdopen(read) as f: for line in f: callback(line) stream.write(line)
def f(s): print("Write")
with monitor(sys.stdout, f) as stream: p = Popen(["ls"], stdout=stream) p.communicate()

import sys import os from subprocess import Popen
class MonitoredStream(): def __init__(self, stream, callback): self.stream = stream self.callback = callback self._read, self._write = os.pipe()
def fileno(self): return self._write
def process(self): os.close(self._write) with os.fdopen(self._read) as f: for line in f: self.callback(line) self.stream.write(line)
def f(s): print("Write")
stream = MonitoredStream(sys.stdout, f) p = Popen(["ls"], stdout=stream) p.communicate() print(stream.process())

