import re

def remove(reg, s, multiline=False):
    flags = [re.M, re.M | re.DOTALL][multiline]
    s,num = re.subn(reg, "", s, flags=flags)
    return s

def classname(s):
    s = remove("\"\"\".*?\"\"\"", s, multiline=True)
    s = remove("\'\'\'.*?\'\'\'", s, multiline=True)
    s = remove("\".*?\"", s)
    s = remove("\'.*?\'", s)

    res = re.search("(^|\s)class\s+(\w+)", s, flags=re.M)
    # print "*** {} -> {}".format(s, res.groups() if res else None)
    if res is None:
        return None
    else:
        return res.group(2)

tests = [ ("class hi()", "hi"), ("class hi(dff)", "hi"), ("class hi ( dff ) :", "hi"), (" class hi ( dff ) :", "hi"), ("class hi", "hi"), ("classf hi", None), ("fclass hi", None), ("'class hi(dd)'", None), ('"class hi(dd)"', None), ("'''class hi(dd)'''", None), ('"""class hi(dd)"""', None), ('"""



class hi(dd)



