from sqlalchemy import * from sqlalchemy.orm import * from sqlalchemy.ext.declarative import declarative_base
Base= declarative_base()
_usergroup_table = Table('usergroup_table', Base.metadata, Column('user_id', Integer, ForeignKey('user.id')), Column('group_id', Integer, ForeignKey('group.id')))
class User(Base): __tablename__ = 'user' id = Column(Integer, primary_key=True) name = Column(String(60), nullable=False, unique=True) group_id = Column(Integer, ForeignKey('group.id'), nullable=False) primary_group = relationship("Group")
@validates('primary_group') def _add_pg(self, key, target): self.groups.add(target) return target
@validates('groups', include_removes=True) def _modify_groups(self, key, target, is_remove): if is_remove and target is self.primary_group: del self.primary_group return target
class Group(Base): __tablename__ = 'group' id = Column(Integer, primary_key=True) name = Column(String(60), nullable=False) users = relationship('User', secondary=_usergroup_table, backref=backref('groups', collection_class=set))
e = create_engine("sqlite://", echo=True) Base.metadata.create_all(e)
s = Session(e)
