class MyTableWidgetItem(QTableWidgetItem): def __lt__(self, other): if ( isinstance(other, QTableWidgetItem) ): my_value, my_ok = self.data(Qt.EditRole).toInt() other_value, other_ok = other.data(Qt.EditRole).toInt()
if ( my_ok and other_ok ): return my_value < other_value
return super(MyTableWidgetItem, self).__lt__(other)
