function normalizeData(data){
  var normalizedData=[];
  var i;
  for(i=0;i<12;i++){
    if(data[i])
        normalizedData[i]=data[i];
    else
        normalizedData[i]=0;
  }
  return normalizedData;
}

function normalizeData(data){
var existingMonths=[];
var normalizedData=[];
var i;
for(i=0;i<data.length;i++){
    var datum=data[i];
    datum[0]=  $.inArray(datum[0],months);
    existingMonths.push(datum[0]);
    normalizedData.push(datum);
}
for(i=0;i< months.length;i++){
    if($.inArray(i,existingMonths)==-1)
        normalizedData.push([i ,0]);
}
return normalizedData;
}

import struct
class TypeConv(object): BSA = '=' # native Byte order, standard Size, with no Alignment def __init__(self, conv_func, fmt_chr): self.__dict__.update(conv_func=conv_func, fmt_chr=fmt_chr)
def pack(self, data): py_value = self.conv_func(data) count = str(len(py_value)) if self.conv_func is str else '' return struct.pack(self.BSA+count+self.fmt_chr, py_value)
type_conv = {'string': TypeConv(str, 's'), 'int32': TypeConv(int, 'i'), 'int64': TypeConv(long, 'q'), 'float32': TypeConv(float, 'f'), }
array = [['string', 'int32', 'string', 'int64', 'float32', 'string', 'string'], ['any string', '19', 'any string', '198732132451654654', '0.6', 'any string', 'any string']]
binary_values = [type_conv[type_id].pack(data) for type_id, data in zip(array[0], array[1]) if type_id in type_conv] # to skip any unknown type_ids print binary_values

#include <numpy/arrayobject.h>

void MyBaseClass::myCallback(const MyRecord& data)
{
    object func = get_override("myCallback");
    if (func) {
        PyArray_Descr* dtype;
        PyObject* op = Py_BuildValue("[(s,s),(s,s),(s,s)]", "myInt", "<u4", "myString", "|S4", "myDouble", "<f8");
        PyArray_DescrConverter(op, &dtype);
        Py_DECREF(op);
        PyObject* pya = PyArray_FromString(const_cast<char*>(reinterpret_cast<const char*>(&data)), sizeof(data), dtype, 1, NULL);
        // PyObject_Print(pya, stdout, 0);
        numeric::array bpa(static_cast<numeric::array>(handle<>(pya)));
        func(bpa);
    }
}

