class Empty(object):

    def __init__(self, **kwargs):
        object.__setattr__(self, '_obj', kwargs)

    def __getattribute__(self, name):
        obj = object.__getattribute__(self, '_obj')
        try:
            return obj[name]
        except KeyError:
            cls_name = object.__getattribute__(self, '__class__').__name__
            raise AttributeError(
                "'%(cls_name)s' object has no attribute '%(name)s'" % locals())

    def __setattr__(self, name, val):
        obj = object.__getattribute__(self, '_obj')
        obj[name] = val

    def __getitem__(self, key):
        return getattr(self, key)

    def __setitem__(self, key, val):
        return setattr(self, key, val)

import numpy as np A = np.random.rand(1000,3,3) def slow_inverse(A): Ainv = np.zeros_like(A)
for i in range(A.shape[0]): Ainv[i] = np.linalg.inv(A[i]) return Ainv
def fast_inverse(A): identity = np.identity(A.shape[2], dtype=A.dtype) Ainv = np.zeros_like(A)
for i in range(A.shape[0]): Ainv[i] = np.linalg.solve(A[i], identity) return Ainv
def fast_inverse2(A): identity = np.identity(A.shape[2], dtype=A.dtype)
return array([np.linalg.solve(x, identity) for x in A])
from numpy.linalg import lapack_lite lapack_routine = lapack_lite.dgesv # Looking one step deeper, we see that solve performs many sanity checks. # Stripping these, we have: def faster_inverse(A): b = np.identity(A.shape[2], dtype=A.dtype)
n_eq = A.shape[1] n_rhs = A.shape[2] pivots = zeros(n_eq, np.intc) identity = np.eye(n_eq) def lapack_inverse(a): b = np.copy(identity) pivots = zeros(n_eq, np.intc) results = lapack_lite.dgesv(n_eq, n_rhs, a, n_eq, pivots, b, n_eq, 0) if results['info'] > 0: raise LinAlgError('Singular matrix') return b
return array([lapack_inverse(a) for a in A])
%timeit -n 20 aI11 = slow_inverse(A) %timeit -n 20 aI12 = fast_inverse(A) %timeit -n 20 aI13 = fast_inverse2(A) %timeit -n 20 aI14 = faster_inverse(A)

