from collections import defaultdict from random import random as rn
class Grid(object): def __init__(self, spacing): self.spacing = spacing self.griddict = defaultdict(int)
def add_point(self, coords): """ `vid`, a voxel id, is a tuple of indices, indicating one grid bin for each dimension, e.g. (1, 5, 2) rule: i_x = int(floor(x_coord / spacing)) """ vid = tuple([int(c//self.spacing) for c in coords]) self.griddict[vid] += 1
def get_point(self, coords): vid = tuple([int(c//self.spacing) for c in coords]) return self.griddict[vid]
def vid_centercoords(self, vid): """ Return the real coordinates in space for a certain voxel, which is identified by its voxel id `vid` (a tuple of indices). """ return tuple([(i-1)*self.spacing + self.spacing/2 for i in vid])
