class Extender(argparse.Action): def __call__(self,parser,namespace,values,option_strings=None): #Need None here incase `argparse.SUPPRESS` was supplied for `dest` dest = getattr(namespace,self.dest,None) #print dest,self.default,values,option_strings if(not hasattr(dest,'extend') or dest == self.default): dest = [] setattr(namespace,self.dest,dest) #if default isn't set to None, this method might be called # with the default as `values` for other arguements which # share this destination. parser.set_defaults(**{self.dest:None})
try: dest.extend(values) except ValueError: dest.append(values)
#another option: #if not isinstance(values,basestring): # dest.extend(values) #else: # dest.append(values) #It's a string. Oops.
def new_parser(args): parser = argparse.ArgumentParser() parser.add_argument('foo', nargs='*',action=Extender) parser.add_argument('--foo', nargs='*', dest='foo', action=Extender) parser.set_defaults(foo = [['spam']]) return parser.parse_args(args.split())
