ii = IntermediateObj({'large': 'nested_dictionary', 'sections': [{}, {}, {}] }) ii.recurse(ii.raw) print ii.raw print ii.sections
# passing an instance of IntermediateObj is not the same as passing a dict (logically) # yet the constructor of MyClass just forwards that instance object to the # baseclasses constructor, while you initially passed a dict to the IntermediateObj # constructor. tada = MyClass(ii) # MyClass inherits the recurse method, but it won't magically be executed unless # you call it, so just instantiating MyClass won't copy those values recursively tada.recurse(ii.raw) # now tada, it copied everything, and notice, I called recurse with ii.raw, which is the # source dictionary, but I'm not even sure if you wanted it that way, it's not clear # define your question more precisely print tada.sections

class IntermediateObj(object):

    def __init__(self, raw):
        self.raw = raw
        self.sections = []
        self.fields = []
        self.elements = []
        self.recurse(raw)

    def recurse(self, items):
        for k,v in items.iteritems():
            if isinstance(v, list):
                getattr(self, k).append(v)
                [self.recurse(i) for i in v]
            else:
                setattr(self, k, v)


class MyClass(IntermediateObj):

    def __init__(self, intermediate_instance):  
        super(MyClass, self).__init__(intermediate_instance.raw)


ii = IntermediateObj({'large': 'nested_dictionary',
                      'sections': [{}, {}, {}]
                    })
print ii.raw
print ii.sections
tada = MyClass(ii)
print tada.sections
print tada.raw

