def find_biggest(items):
    for w in reversed(range(len(items)+1)):
        for c in itertools.combinations(items, w):
            indices, slots = zip(*c)
            for p in itertools.product(*slots):
                if len(set(p)) == len(p):
                    return dict(zip(indices, p))

