class MyIterator:
    def __init__(self):
        self.i = 10
    def next(self):
        self.i -= 1
        if self.i == 0:
            raise StopIteration
        else:
            return self.i

def __iter__(self):
    for i in range(10)
        yield i

def __iter__(self):
    stack = [('', self.root)]
    while stack:
        prefix, d = stack.pop()
        for k, v in d.items():
            if k == 'value':
                yield prefix, v
            else:
                stack.append((prefix + k, v))

class DummyPopen(object):
    def __init__(self, *args, **kw):
        print('Popen({})'.format(
            ', '.join([repr(arg) for arg in args] +
                      ['{}={!r}'.format(*pair) for pair in kw.items()])))
        self.returncode = 1

    def wait(self):
        return 1

    # et cetera

def Popen(*args, testmode=False, **kw):
    if testmode:
        return DummyPopen(*args, **kw)
    return subprocess.Popen(*args, **kw)

if testmode:
    Popen = DummyPopen
else:
    Popen = subprocess.Popen

