def get_cnt(lVals):
    d = dict(zip(lVals, [0]*len(lVals)))
    for x in lVals:
        d[x] += 1
    return d

def pagesubs(N, *subs):
    assert type(N)==str
    with open(N,'r') as F:
        content = F.read()
    return content.format(subs)

