import types

class NoWrap(object):

    def __init__(self, obj, default=None):
        self._obj = obj 
        self._default = default

    def __getitem__(self, key):
        if type(key) is types.IntType:
            if key < 0:
                return self._default

        return self._obj.__getitem__(key)

