class TipButton(wx.Button): """Subclass of wx.Button, has the same functionality. Allows user to set Status Bar help by using SetStatusText method. """ current = None
def __init__(self, *args, **kwargs): wx.Button.__init__(self, *args, **kwargs)
def SetStatusText(self, help_string, status_bar_frame): self.help_string = help_string self.status_bar_frame = status_bar_frame self.Bind(wx.EVT_ENTER_WINDOW, self._ShowHelp) self.Bind(wx.EVT_LEAVE_WINDOW, self._HideHelp)
def _ShowHelp(self, e): TipButton.current = self.GetLabel() self.status_bar_frame.SetStatusText(self.help_string)
def _HideHelp(self, e): if self.GetLabel() == TipButton.current: self.status_bar_frame.SetStatusText("")
