class ColorMap: startcolor = () endcolor = () startmap = 0 endmap = 0 colordistance = 0 valuerange = 0 ratios = []
def __init__(self, startcolor, endcolor, startmap, endmap): self.startcolor = np.array(startcolor) self.endcolor = np.array(endcolor) self.startmap = float(startmap) self.endmap = float(endmap) self.valuerange = float(endmap - startmap) self.ratios = (self.endcolor - self.startcolor) / self.valuerange
def __getitem__(self, value): color = tuple(self.startcolor + (self.ratios * (value - self.startmap))) return (int(color[0]), int(color[1]), int(color[2]))

from math import floor
def build_rfc3339_phrase(datetime_obj): datetime_phrase = datetime_obj.strftime('%Y-%m-%dT%H:%M:%S') us = datetime_obj.strftime('%f')
seconds = datetime_obj.utcoffset().total_seconds()
if seconds is None: datetime_phrase += 'Z' else: # Append: decimal, 6-digit uS, -/+, hours, minutes datetime_phrase += ('.%.6s%s%02d:%02d' % ( us, ('-' if seconds < 0 else '+'), abs(int(floor(seconds / 3600))), abs(seconds % 3600) ))
return datetime_phrase

def nestedness(struct):
    if isinstance(struct, list):
        return max([0] + [nestedness(i) for i in struct]) + 1
    if isinstance(struct, dict):
        return max([0] + [nestedness(i) for i in struct.values()])+1
    return 1

for lines in chain(*infiles):
     fout.write(lines)
     if not lines.endswith('\n'):
         fout.write('\n')

def f(n):
    a, b = 0, 1
    for i in range(0, n):
        a, b = b, a + b
    return a

l = [["('N', '', 'SHOWERS', '', 'RD', '11734', '11734', '', '')", "('61060', '61060')"], ['', '', 'NORA', '', 'RD', '10301', '10999', '10400', '10998', '61089', '61089']]

l2 = [['N', '', 'SHOWERS', '', 'RD', '11734', '11734', '', '', '61060', '61060'], ['', '', 'NORA', '', 'RD', '10301', '10999', '10400', '10998', '61089', '61089']]

import ast
from itertools import imap, chain


def is_tuple_string_row(L):
    """Return whether list L is a list of strings containing tuples"""
    return len(L)==2 and all(s.startswith("('") and s.endswith("')") for s in L)


def normalized_list(L):
    """Yield lists in sequence L as lists of strings

    Normalizes a list of strings of tuples down to a simple list of strings
    """
    for item in L:
        if is_tuple_string_row(item):
            yield list(chain.from_iterable(imap(ast.literal_eval, item)))
        else:
            yield item


# You can iterate efficiently
for item in normalized_list(l):
    print item

# or you can put the whole thing to a single new list
aslist = list(normalized_list(l))

# verify results
assert aslist == l2

def buildLink2(self):
    temp=1
    while temp<10:
        self=self.next
        self=Node(temp)
        temp+=1

def build_link(first_node):

    cur_node = first_node
    for tmp in range(10):
        cur_node.next = Node(tmp)
        cur_node = cur_node.next

