import multiprocessing as mp
import tables as pt


num_arrays = 100
num_processes = mp.cpu_count()
num_simulations = 1000
sentinel = None


def Simulation(inqueue, output):
    for ii in iter(inqueue.get, sentinel):
        output.put(('createGroup', ('/', 'A%s' % ii)))
        for i in range(num_arrays):
            output.put(('createArray', ('/A%s' % ii, 'B%s' % i, [ii, i])))


def handle_output(output):
    hdf = pt.openFile('simulation.h5', mode='w')
    while True:
        args = output.get()
        if args:
            method, args = args
            getattr(hdf, method)(*args)
        else:
            break
    hdf.close()

if __name__ == '__main__':
    output = mp.Queue()
    inqueue = mp.Queue()
    jobs = []
    proc = mp.Process(target=handle_output, args=(output, ))
    proc.start()
    for i in range(num_processes):
        p = mp.Process(target=Simulation, args=(inqueue, output))
        jobs.append(p)
        p.start()
    for i in range(num_simulations):
        inqueue.put(i)
    for i in range(num_processes):
        # Send the sentinal to tell Simulation to end
        inqueue.put(sentinel)
    for p in jobs:
        p.join()
    output.put(None)
    proc.join()

import multiprocessing as mp import tables as pt
num_arrays = 100 num_processes = mp.cpu_count() num_simulations = 1000
def Simulation(ii): result = [] result.append(('createGroup', ('/', 'A%s' % ii))) for i in range(num_arrays): result.append(('createArray', ('/A%s' % ii, 'B%s' % i, [ii, i]))) return result
def handle_output(result): hdf = pt.openFile('simulation.h5', mode='a') for args in result: method, args = args getattr(hdf, method)(*args) hdf.close()
if __name__ == '__main__': # clear the file hdf = pt.openFile('simulation.h5', mode='w') hdf.close() pool = mp.Pool(num_processes) for i in range(num_simulations): pool.apply_async(Simulation, (i, ), callback=handle_output) pool.close() pool.join()

