def check(exceptions, msg, handler):
    def deco(func):
        def wrapped(*args, **kwargs):
            try:
                return func(*args, **kwargs)
            except exceptions as err:
                print "Logged error:", msg
                return handler(err)
        return wrapped
    return deco

def handler(err):
    return []

@check(ZeroDivisionError, "divide by zero", handler)
