def keyfunc(timestamp, interval = 5*60): # defined a key function. # 1. parse the datetime string to datetime object # 2. count the time delta (seconds) # 3. divided the time delta with interval, which is (6*60) here xt = datetime(2013, 4,3) dt = datetime.strptime(timestamp, '%d/%m/%Y %H:%M:%S') delta_second = int((dt - xt).total_seconds()) normalize_second = (delta_second / interval) * interval return xt + timedelta(seconds=normalize_second)
