struct example {
    char* data;
    int len;          // of data buffer
    double* doubles;
    int count;        // of doubles
};

DLL_API void func(struct example* p);

#include <stdio.h>
#define DLL_EXPORTS
#include "x.h"

void func(struct example* p)
{
    int i;
    strcpy_s(p->data,p->len,"hello, world!");
    for(i = 0; i < p->count; i++)
        p->doubles[i] = 1.1 * (i + 1);
}

import ctypes
class Example(ctypes.Structure):
_fields_ = [ ('data',ctypes.POINTER(ctypes.c_char)), ('len',ctypes.c_int), ('doubles',ctypes.POINTER(ctypes.c_double)), ('count',ctypes.c_int)]
def __init__(self,length,count): self.data = ctypes.cast(ctypes.create_string_buffer(length),ctypes.POINTER(ctypes.c_char)) self.len = length self.doubles = (ctypes.c_double * count)() self.count = count
def __repr__(self): return 'Example({},[{}])'.format( ctypes.string_at(self.data), ','.join(str(self.doubles[i]) for i in range(self.count)))
class Dll:
def __init__(self): self.dll = ctypes.CDLL('x') self.dll.func.argtypes = [ctypes.POINTER(Example)] self.dll.func.restype = None
def func(self,ex): self.dll.func(ctypes.byref(ex))
d = Dll() e = Example(20,5) print('before:',e) d.func(e) print ('after:',e)

